import React, { useState } from "react";
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Select,
  MenuItem,
  FormControl,
  ToggleButtonGroup,
  ToggleButton,
  Modal,
  Button,
} from "@mui/material";
import WarningIcon from "@mui/icons-material/Warning";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import SampleImg from "../assets/sample.jpg";
import TuneIcon from "@mui/icons-material/Tune";
import tmp from '../assets/example.json'

const initialAlerts = [
  {
    ip_address: '127.0.1.1',
    timestamp: '2025-02-27T11:55:21.200775',
    camera_sector: 'Sector A',
    status: 'normal', //hazard
    image: `data:image/png;base64,${tmp.image}`
    // id: 1,
    // camera: 3,
    // section: 4,
    // status: "active",
    // timestamp: "2024-02-26 12:30",
    // annotations: "Anomaly detected",
    // deviceId: "D123",
  },
  // {
  //   ip_address: '123',
  //   timestamp: '2024-02-25 15:45',
  //   camera_sector: '1',
  //   status: 'Active',
  //   image: '',
  // },
  {
    ip_address: '127.0.1.1',
    timestamp: '2025-02-27T11:55:21.200775',
    camera_sector: 'Sector A',
    status: 'normal', //hazard
    image: `data:image/png;base64,${tmp.image}`
  },
  {
    ip_address: '127.0.1.1',
    timestamp: '2025-02-27T11:55:21.200775',
    camera_sector: 'Sector A',
    status: 'normal', //hazard
    image: `data:image/png;base64,${tmp.image}`
  },
  {
    ip_address: '127.0.1.1',
    timestamp: '2025-02-27T11:55:21.200775',
    camera_sector: 'Sector A',
    status: 'normal', //hazard
    image: `data:image/png;base64,${tmp.image}`
  },
  {
    ip_address: '127.0.1.1',
    timestamp: '2025-02-27T11:55:21.200775',
    camera_sector: 'Sector A',
    status: 'normal', //hazard
    image: `data:image/png;base64,${tmp.image}`
  },
  {
    ip_address: '127.0.1.1',
    timestamp: '2025-02-27T11:55:21.200775',
    camera_sector: 'Sector A',
    status: 'normal', //hazard
    image: `data:image/png;base64,${tmp.image}`
  },
];

export default function Alerts() {
  const [alerts, setAlerts] = useState(
    initialAlerts.map((alert, index) => ({
      ...alert,
      id: index, // Assign a unique ID
    }))
  );
  const [statusFilter, setStatusFilter] = useState("all");
  const [cameraFilter, setCameraFilter] = useState("all");
  const [sectionFilter, setSectionFilter] = useState("all");
  const [selectedAlert, setSelectedAlert] = useState(null);

  const handleAlertClick = (alert) => {
    setSelectedAlert(alert);
  };

  const handleCloseModal = () => {
    setSelectedAlert(null);
  };

  const handleResolve = () => {
    setAlerts(
      alerts.map((alert) =>
        alert.id === selectedAlert.id ? { ...alert, status: "hazard" } : alert
      )
    );
    setSelectedAlert(null);
  };

  const filteredAlerts = alerts.filter((alert) => {
    return (
      (statusFilter === "all" || alert.status === statusFilter) &&
      (cameraFilter === "all" || alert.camera_sector === cameraFilter) 
      // && (sectionFilter === "all" || alert.section === sectionFilter)
    );
  });

  return (
    <Box display="flex" p={3}>
      <Box flex={3}>
        <Typography variant="h4" gutterBottom>
          Alerts
        </Typography>
        <List>
          {filteredAlerts.length > 0 ? (
            filteredAlerts.map((alert, index) => (
              <React.Fragment key={alert.id}>
                <ListItem button onClick={() => handleAlertClick(alert)}>
                  <ListItemIcon>
                    {alert.status === "active" ? (
                      <WarningIcon sx={{ color: "red" }} />
                    ) : (
                      <CheckCircleIcon sx={{ color: "green" }} />
                    )}
                  </ListItemIcon>
                  <ListItemText
                    primary={`Alert #${alert.id}. Camera ${alert.camera}. Section ${alert.section}.`}
                    sx={{ color: alert.status === "active" ? "red" : "blue" }}
                  />
                  <Box>
                    <ListItemText
                      primary={"Posted 10 min ago"}
                      sx={{ color: "gray" }}
                    />
                  </Box>
                </ListItem>
                {index < filteredAlerts.length - 1 && <Divider />}
              </React.Fragment>
            ))
          ) : (
            <Typography sx={{ color: "gray", mt: 2 }}>
              No alerts found.
            </Typography>
          )}
        </List>
      </Box>

      {/* Filters */}
      <Box flex={1} ml={3} sx={{ borderLeft: "1px solid lightgray", pl: 3 }}>
        <Box
          display={"flex"}
          alignItems={"center"}
          justifyItems={"center"}
          mb={2}
          gap={1}
        >
          <Typography variant="h5">Filters</Typography>
          <TuneIcon
            sx={{
              alignSelf: "center",
              justifyContent: "center",
              alignItems: "center",
            }}
          />
        </Box>
        <Typography variant="subtitle1">Status</Typography>
        <ToggleButtonGroup
          value={statusFilter}
          exclusive
          onChange={(event, newValue) => setStatusFilter(newValue || "all")}
          sx={{ mb: 2 }}
        >
          <ToggleButton value="all">All</ToggleButton>
          <ToggleButton value="active" sx={{ color: "red" }}>
            Active
          </ToggleButton>
          <ToggleButton value="resolved" sx={{ color: "green" }}>
            Resolved
          </ToggleButton>
        </ToggleButtonGroup>

        <FormControl fullWidth sx={{ mb: 2 }}>
          <Typography variant="subtitle1">Camera</Typography>
          <Select
            value={cameraFilter}
            onChange={(e) => setCameraFilter(e.target.value)}
          >
            <MenuItem value="all">All</MenuItem>
